import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';

class EditarTarjeta extends Component {


    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('text_tarjeta').value
        }

        this.props.editarTarjeta(data);
    }
    delete() {

        this.props.eliminarTarjeta(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar tarjeta de conocimiento</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <p>Texto</p>
                                            <input id="text_tarjeta" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />
                                        </div>
                                        <div className="input-field col s12">
                                            <p>Título</p>
                                            <input id="titulo_tarjeta" type="text" className="validate" />
                                        </div>
                                        <div className="row">
                                            <div className="input-field col s6">
                                                <p>Imagen</p>
                                                <input id="url_recurso" onChange={e => this.handleChange(e)} src={this.props.location.path_imagen} type="file" className="validate" />
                                            </div>
                                            <div className="card">
                                                <div className="card-image">
                                                    <img id="output" className="col l6" alt="imagen" />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="row">
                                            <div className="input-field col s6">
                                                <p>¿A qué subtema pertenece?</p>
                                                <select id="subtemas" className="browser-default">
                                                    {this.showSubtemas()}
                                                </select>
                                            </div>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/tarjetas'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/tarjetas'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/tarjetas' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(EditarTarjeta);
