const INITIAL_STATE = {
    preguntas: [
        {
            'id': 1,
            'texto': 'texto pregunta 1'
        },
        {
            'id': 2,
            'texto': 'texto pregunta 2'
        },
        {
            'id': 3,
            'texto': 'texto pregunta 3'
        },
        {
            'id': 4,
            'texto': 'texto pregunta 4'
        },
    ],
    lastId: 4
};

export default (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'traer_preguntas':
            return { ...state, preguntas: action.payload }
        case 'crear_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        case 'editar_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        case 'eliminar_pregunta':
            return {
                ...state,
                preguntas: action.payload
            }
        default:
            return state;
    }
}
