import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'answers';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerRespuestas = () => async (dispacth) => {       
    
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_respuestas',
        payload: respuesta.data.respuestas
    })
}

export const crearRespuesta = (respuesta, respuestas) => async (dispacth) => {
    
   
    const r = await instance.post(url, respuesta);      
    respuestas.push(r.data.respuestas);
    dispacth({
        type: 'crear_respuesta',
        payload: respuestas
    })
}

export const editarRespuesta = (data) => async (dispacth) => {
    
    
    await instance.put(url, data);
    const respuesta = await instance.get();   
    
    dispacth({
        type: 'editar_respuesta',
        payload: respuesta.data.respuestas
    })
}

export const eliminarRespuesta = (id) => async (dispacth) => {
    
   
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get();  
    
    dispacth({
        type: 'eliminar_respuesta',
        payload: respuesta.data.respuestas
    })
}
