import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as respuestasTriviaActions from '../../../../actions/respuestasTriviaActions';

class EditarRespuestaT extends Component {


    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('text_respuestaT').value
        }

        this.props.editarRespuestaT(data);
    }
    delete() {

        this.props.eliminarRespuestaT(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar respuesta</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_respuestaT" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/respuestasT'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/respuestasT'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/respuestasT' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.respuestasTReducers;
}
export default connect(mapToStateProps, respuestasTriviaActions)(EditarRespuestaT);
