import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as subtemasActions from '../../../../actions/subtemasActions';

class EditarSubtema extends Component {


    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('text_subtema').value,
            "modulo_id": document.getElementById('modulos').value
        }

        this.props.editarSubtema(data);
    }

    delete() {

        this.props.eliminarSubtema(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Editar subtema</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_subtema" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />
                                        </div>
                                    </div>
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <p>MÃ³dulo :</p>
                                            <select id="modulos" className="browser-default" defaultValue={this.props.location.modulo}>
                                                {this.showModulos()}
                                            </select>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/subtemas'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/subtemas'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/subtemas' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.subtemasReducers;
}
export default connect(mapToStateProps, subtemasActions)(EditarSubtema);
