import React, { Component } from 'react';
import './IndexRecursos.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as recursosActions from '../../../../actions/recursosActions';
import Spinner from '../../general/Spinner';

class IndexRecursos extends Component {


    componentDidMount() {
        this.props.traerRecursos(this.props.recursos);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearRecurso' id="crear_recurso" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Nombre del recurso</th>
                            <th scope="col">Url</th>
                            <th>Imagen</th>
                            <th scope="col">Tama√±o</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }

    ponerFilas = () => this.props.recursos.map((recurso, index) => (
        <tr key={recurso.id}>

            <th scope="row">
                {index + 1}
            </th>            
            <td>
                {recurso.name}
            </td>
            <td>
                {recurso.path}
            </td>
            <td>
                <img id="tableImg" src={recurso.path} alt="imagen"></img>
            </td>
            <td>
                {recurso.size / 1000} KB
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarRecurso",
                    index: index,
                    id: recurso.id,
                    nombre: recurso.name,
                    url: recurso.path,
                    tamano: recurso.size
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-recursos" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de recursos</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.recursosReducers;
}
export default connect(mapToStateProps, recursosActions)(IndexRecursos);
