import React, { Component } from 'react';
import './IndexSubtemas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as subtemasActions from '../../../../actions/subtemasActions';
import Spinner from '../../general/Spinner';

class IndexSubtemas extends Component {


    componentDidMount() {
        this.props.traerSubtemas(this.props.subtemas);
        this.props.traerModulos();
    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    getNombreModulo = (id) => {
        let nombre = '';
        this.props.modulos.forEach(element => {
            if (element.id == id) {
                nombre = element.texto
            }
        });
        return nombre;
    }

    showTable = () => {
        if (this.props.cargando)
            return <Spinner />
        return (
            <div>
                <div className="a-right">
                    <Link to='/crearSubtema' id="crear_subtema" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                </div>
                <table id="table-users" className="striped centered">
                    <thead>
                        <tr>
                            <th scope="col">#</th>                            
                            <th scope="col">Título del subtema</th>
                            <th scope="col">Módulo</th>
                            <th scope="col">Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.ponerFilas()}
                    </tbody>
                </table>
            </div>
        )
    }

    ponerFilas = () => this.props.subtemas.map((subtema, index) => (
        <tr key={subtema.id}>

            <th scope="row">
                {index + 1}
            </th>            
            <td>
                {subtema.texto}
            </td>
            <td>
                {this.getNombreModulo(subtema.modulo_id)}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarSubtema",
                    index: index,
                    id: subtema.id,
                    texto: subtema.texto,
                    modulo: subtema.modulo_id
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-subtemas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Listado de subtemas</h5></div>
                        <div className="card-content">
                            {this.showTable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.subtemasReducers;
}
export default connect(mapToStateProps, subtemasActions)(IndexSubtemas);
