import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'questionsTrivia';
const instance = axios.create({
    baseURL: url,
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerPreguntasT = () => async (dispacth) => {   
    dispacth({
        type: 'cargando'        
    })
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_preguntasT',
        payload: respuesta.data.preguntas
    })
}

export const crearPreguntaT = (pregunta, preguntas) => async (dispacth) => {    
    
    const respuesta = await instance.post(url, pregunta);   
    preguntas.push(respuesta.data.pregunta);
    dispacth({
        type: 'crear_preguntaT',
        payload: preguntas
    })
}

export const editarPreguntaT = (data) => async (dispacth) => {
    
    await instance.put(url, data);
    const respuesta = await instance.get();     
    dispacth({
        type: 'editar_preguntaT',
        payload: respuesta.data.preguntas
    })
}

export const eliminarPreguntaT = (id) => async (dispacth) => {
   
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    
    dispacth({
        type: 'eliminar_preguntaT',
        payload: respuesta.data.preguntas
    })
}
