export const traerRespuestasT = (respuestasT) => (dispacth) => {       
    
    dispacth({
        type: 'traer_respuestasT',
        payload: respuestasT
    })
}

export const crearRespuestaT = (respuestaT, respuestasT) => async (dispacth) => {
    
   
    respuestasT.push(respuestaT);
    dispacth({
        type: 'crear_respuestaT',
        payload: respuestasT
    })
}

export const editarRespuestaT = (data, respuestasT) => async (dispacth) => {
    
    respuestasT[data.index].texto = data.texto;
    respuestasT[data.index].id = data.id;
    
    dispacth({
        type: 'editar_respuestaT',
        payload: respuestasT
    })
}

export const eliminarRespuestaT = (index, respuestasT) => async (dispacth) => {
    
    respuestasT.splice(index, 1);
    dispacth({
        type: 'eliminar_respuestaT',
        payload: respuestasT
    })
}
