import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'modulos';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerModulos = () => async (dispacth) => {       
    
    const respuesta = await instance.get();    
    
    dispacth({
        type: 'traer_modulos',
        payload: respuesta.data.modulos
    })
}

export const crearModulo = (modulo, modulos) => async (dispacth) => {
        
    const r = await instance.post(url, modulo);      
    modulos.push(r.data.modulo);    
    dispacth({
        type: 'crear_modulo',
        payload: modulos
    })
}

export const editarModulo = (data) => async (dispacth) => {
      
    await instance.put(url, data);
    const respuesta = await instance.get(url);   
    
    dispacth({
        type: 'editar_modulo',
        payload: respuesta.data.modulos
    })
}

export const eliminarModulo = (id) => async (dispacth) => {
    
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_modulo',
        payload: respuesta.data.modulos
    })
}
