import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as modulosActions from '../../../../actions/modulosActions';

class EditarModulo extends Component {


    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('text_modulo').value
        }

        this.props.editarModulo(data);
    }

    componentDidMount() {


    }



    delete() {

        this.props.eliminarModulo(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_modulo" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />
                                        </div>
                                        <div className="input-field col s3">
                                            <label>Prioridades</label>
                                            <select id="show_prioridades">
                                            </select>
                                        </div>

                                    </div>

                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/modulos'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/modulos'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/modulos' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.modulosReducers;
}
export default connect(mapToStateProps, modulosActions)(EditarModulo);
