import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'tarjetas';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerTarjetas = () => async (dispacth) => {       
    dispacth({
        type: 'cargando'        
    })
    const respuesta = await instance.get();    
    
    dispacth({
        type: 'traer_tarjetas',
        payload: respuesta.data.tarjetas
    })
}

export const crearTarjeta = (tarjeta, tarjetas) => async (dispacth) => {
        
    const r = await instance.post(url, tarjeta);      
    tarjetas.push(r.data.tarjeta);    
    dispacth({
        type: 'crear_tarjeta',
        payload: tarjetas
    })
}

export const editarTarjeta = (data) => async (dispacth) => {
      
    await instance.put(url, data);
    const respuesta = await instance.get(url);   
    
    dispacth({
        type: 'editar_tarjeta',
        payload: respuesta.data.tarjetas
    })
}

export const eliminarTarjeta = (id) => async (dispacth) => {
    
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_tarjeta',
        payload: respuesta.data.tarjetas
    })
}
