import React, { Component } from 'react';
import './Menu.css';
import { Link } from 'react-router-dom';
class Menu extends Component {

    highlight = (e) => {
        
        /*let opts = document.getElementsByClassName('option');
        opts.style.color = "rgba(0,0,0,0.87)";
        opts.style.fontSize = "14px";*/
        let opts = document.getElementsByClassName('option');
        console.log(opts[0].id);
        let opt = document.getElementById(e.target.id);
        let liparent = opt.parentElement;
        opt.style.color ="#fff";
        liparent.style.backgroundColor = "#311b92";    
        opts.forEach(element => {
            if(element.id != e.target.id){
                element.style.color = "rgba(0,0,0,0.87)";
                element.parentElement.style.backgroundColor = "#fff";
            }
            //console.log(element.id);
        });    
        
    }
    render() {
        return (
            <aside>
                <ul id="slide-out" className="sidenav sidenav-fixed">
                    <li id="logo">
                        <Link to='/' id="logo-container">
                            <img alt='logo' id="img-logo" src={process.env.PUBLIC_URL + '/img/wll.png'} />
                        </Link>
                    </li>
                    <li className="opt-li"><Link id="opt1" className="option" to='/' onClick={(e) => this.highlight(e)}>Usuarios</Link></li>
                    <li className="opt-li"><Link id="opt2" className="option" to='/preguntas' onClick={(e) => this.highlight(e)}>Preguntas</Link></li>
                    <li className="opt-li"><Link id="opt3" className="option" to='/respuestas'  onClick={(e) => this.highlight(e)}>Respuestas</Link></li>
                    <li className="opt-li"><Link id="opt4" className="option" to='/modulos'  onClick={(e) => this.highlight(e)}>Modulos</Link></li>
                    <li className="opt-li"><Link id="opt5" className="option" to='/subtemas'  onClick={(e) => this.highlight(e)}>Subtemas</Link></li>
                    <li className="opt-li"><Link id="opt6" className="option" to='/tarjetas'  onClick={(e) => this.highlight(e)}>Tarjetas de conocimiento</Link></li>
                    <li className="opt-li"><Link id="opt7" className="option" to='/tarjetasSM' onClick={(e) => this.highlight(e)}>Tarjetas saber mas</Link></li>
                    <li className="opt-li"><Link id="opt8" className="option" to='/preguntasT' onClick={(e) => this.highlight(e)}>Preguntas trivia</Link></li>
                    <li className="opt-li"><Link id="opt9" className="option" to='/respuestasT' onClick={(e) => this.highlight(e)}>Respuestas trivia</Link></li>
                    <li className="opt-li"><Link id="opt10" className="option" to='/recursos' onClick={(e) => this.highlight(e)}>Recursos</Link></li>
                    <li className="opt-li"><Link id="opt11" className="option" to='/exportar' onClick={(e) => this.highlight(e)}>Exportar</Link></li>
                </ul>
            </aside>
        )
    }
}

export default Menu;
