import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasActions from '../../../../actions/preguntasActions';

class CrearPregunta extends Component {


    constructor(props) {
        super(props);

        this.state = {
            data: '<p>Escriba el <em>texto</em> de la <strong>pregunta</strong></p>',
            positions: [],
            positionsLastSelect: [],
            scores: [],
            scoresLastSelect: [],
            scores: [0, 1, 2]
        };

        this.handleChange = this.handleChange.bind(this);
        this.onEditorChange = this.onEditorChange.bind(this);
    }

    onEditorChange(evt) {
        this.setState({
            data: evt.editor.getData()
        });
    }

    handleChange(changeEvent) {
        this.setState({
            data: changeEvent.target.value
        });
    }

    crear = () => {
        //let idPregunta = this.props.preguntas; 
        console.log(this.props.preguntas);

        let data = {
            "id": this.props.lastId + 1,
            "texto": this.state.data
        }
        this.props.crearPregunta(data, this.props.preguntas);

    }

    positionChange = (e, positions) => {
        let postionsEnables = positions;
        let pos = postionsEnables.indexOf(e.target.value);
        postionsEnables.splice(pos, 1);
        if (e.target.id == 1) {
            this.setState({
                positions: postionsEnables
            });
        } else {
            this.setState({
                positionsLastSelect: postionsEnables
            });
        }

    }

    scoreChange = (e, scores) => {
        let scoresEnables = scores;
        let pos = scoresEnables.indexOf(e.target.value);
        scoresEnables.splice(pos, 1);
        if (e.target.id == 1) {
            this.setState({
                scores: scoresEnables
            });
        } else {
            this.setState({
                scoresLastSelect: scoresEnables
            });
        }

    }

    showOptions = (options) => (
        options.map((op, index) => (
            <option key={index} value={op}>{op}</option>
        ))
    )




    showScores = (e, item) => {

        console.log(e.target.value);
    }

    /*ponerGeneros = () => this.state.generos.map((genero, index) => (
        <option value={genero.id_genero}>
            {genero.nombre_genero}
        </option>
    ));*/

    render() {

        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <span className="card-title"><h4>Pregunta</h4></span>
                            <div className="row">
                                <form className="col s12">
                                    <label>Texto de la pregunta</label>
                                    <CKEditor
                                        data={this.state.data}
                                        onChange={this.onEditorChange} />
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                        </div>
                    </div>
                    <div className="card-panel">
                        <div className="card-content">
                            <span className="card-title"><h4>Respuestas</h4></span>
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id="1" className="browser-default" onChange={(e) => { this.positionChange(e, ['a', 'b', 'c']) }}>
                                            <option value="" disabled selected>Posición</option>
                                            <option value="a">a</option>
                                            <option value="b">b</option>
                                            <option value="c">c</option>
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select id="4" className="browser-default" onChange={(e) => { this.scoreChange(e, [0, 1, 2]) }}>
                                            <option value="" disabled selected>Puntaje</option>
                                            <option value="0">0</option>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id='2' className="browser-default" onChange={(e) => { this.positionChange(e, this.state.positions.slice()) }}>
                                            <option value="" disabled selected>Posición</option>
                                            {this.showOptions(this.state.positions)}
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select id="5" className="browser-default" onChange={(e) => { this.scoreChange(e, this.state.scores.slice()) }}>
                                            <option value="" disabled selected>Puntaje</option>
                                            {this.showOptions(this.state.scores)}
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s12">
                                    <div className="input-field col s1">
                                        <select id='3' className="browser-default">
                                            <option value="" disabled selected>Posición</option>
                                            {this.showOptions(this.state.positionsLastSelect)}
                                        </select>
                                    </div>
                                    <div className="input-field col s10">
                                        <input id="texto_respuesta" type="text" className="validate" />
                                        <label htmlFor="texto_respuesta">Texto de la respuesta</label>
                                    </div>
                                    <div className="input-field col s1">
                                        <select className="browser-default">
                                            <option value="" disabled selected>Puntaje</option>
                                            <option value="0">0</option>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                        </select>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntas'>Guardar respuestas</Link>
                            <Link className="btn btn-primary btn_opts" to='/'>Cancelar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasReducers;
}
export default connect(mapToStateProps, preguntasActions)(CrearPregunta);
