import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


const token = window.localStorage.getItem('token');

class Editar extends Component {

    
    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": document.getElementById('texto-pregunta').value           
        }
        const instance = axios.create({
            baseURL: 'http://localhost:8000/api/update',
            timeout: 1000,
            headers: { 'Authorization': 'Bearer ' + token }
        });

        instance.post('http://localhost:8000/api/update', data)
            .then(res => {
                console.log(res.data);
            });

    }

    componentDidMount() {
        console.log(this.props.location.nombres);

    }

    /*ponerGeneros = () => this.state.generos.map((genero, index) => (
        <option value={genero.id_genero}>
            {genero.nombre_genero}
        </option>
    ));*/

    delete() {
        const instance = axios.create({
            baseURL: 'http://localhost:8000/api/delete',
            timeout: 1000,
            headers: { 'Authorization': 'Bearer ' + token }
        });

        instance.post('http://localhost:8000/api/delete', { "id": this.props.location.id })
            .then(res => {
                console.log(res.data);
            });
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="row">
                                        <div className="input-field col s6">
                                            <input id="text_pregunta" type="text" className="validate" placeholder={this.props.location.texto} defaultValue={this.props.location.texto} />                                            
                                        </div>                                        
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Editar;
