import React, { Component } from 'react';
import './IndexSubtemas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as subtemasActions from '../../../../actions/subtemasActions';

class IndexSubtemas extends Component {

    
    componentDidMount() {
        this.props.traerSubtemas(this.props.subtemas);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    ponerFilas = () => this.props.subtemas.map((subtema, index) => (
        <tr key={subtema.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {subtema.id}
            </td>
            <td>
                {subtema.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarSubtema",
                    index: index,
                    id: subtema.id,
                    texto: subtema.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-subtemas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="a-right">
                                <Link to='/crearSubtema' id="crear_subtema" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                            </div>
                            <table id="table-users">
                                <thead>
                                    <tr>
                                        <th scope="col">#</th>
                                        <th scope="col">Id</th>
                                        <th scope="col">Texto del subtema</th>
                                        <th scope="col">Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.ponerFilas()}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.subtemasReducers;
}
export default connect(mapToStateProps, subtemasActions)(IndexSubtemas);
