import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import Spinner from '../general/Spinner';
import * as subtemasActions from '../../../actions/subtemasActions';
import axios from 'axios';
class Oportunidades extends Component {






    showOportunidades = () => {

        if (this.props.opts != '') {
            return this.props.opts.map((op, index) => (
                <div key={index} className="col s6 l6">
                    <div className="card">

                        <span className="card-title">{op.objective}</span>

                        <div className="card-content">
                            <p>{op.type}</p>
                        </div>
                    </div>
                </div>
            ))
        }

    }

    delete() {

        this.props.eliminarSubtema(this.props.location.id);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Oportunidades</h5></div>
                        <div className="card-content">
                            <div className="row">
                                {this.showOportunidades()}
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-small btn_opts" onClick={this.editar} to='/subtemas'>Guardar</Link>
                            <Link className="btn btn-small btn_opts" to='/subtemas'>Cancelar</Link>
                            <Link className="btn btn-small btn_opts" to='/subtemas' onClick={() => { this.delete() }}>Eliminar</Link>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapToStateProps = (reducers) => {
    return reducers.subtemasReducers;
}
export default connect(mapToStateProps, subtemasActions)(Oportunidades);
