import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'questions';
const instance = axios.create({
    baseURL: url,
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerPreguntas = () => async (dispacth) => {       
    dispacth({
        type: 'cargando',        
    })
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_preguntas',
        payload: respuesta.data.preguntas
    })
}

export const crearPregunta = (pregunta, preguntas) => async (dispacth) => {
    
   
    const respuesta = await instance.post(url, pregunta);   
    preguntas.push(respuesta.data.pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data) => async (dispacth) => {
    

    await instance.put(url, data);
    const respuesta = await instance.get();     
    dispacth({
        type: 'editar_pregunta',
        payload: respuesta.data.preguntas
    })
}

export const eliminarPregunta = (id) => async (dispacth) => {
   
   
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get();  
    
    dispacth({
        type: 'eliminar_pregunta',
        payload: respuesta.data.preguntas
    })
}
