import React, { Component } from 'react';
import './IndexTarjetas.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as tarjetasActions from '../../../../actions/tarjetasActions';
import Spinner from '../../general/Spinner';

class IndexTarjetas extends Component {

    
    componentDidMount() {
        this.props.traerTarjetas(this.props.tarjetas);

    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    showtable = () => {
        if(this.props.cargando)
            return <Spinner/>
        return(
            <div>

            </div>
        )
    }
    ponerFilas = () => this.props.tarjetas.map((tarjeta, index) => (
        <tr key={tarjeta.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {tarjeta.id}
            </td>
            <td>
                {tarjeta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editarTarjeta",
                    index: index,
                    id: tarjeta.id,
                    texto: tarjeta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {

        return (

            <div id="index-tarjetas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            {this.showtable()}
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.tarjetasReducers;
}
export default connect(mapToStateProps, tarjetasActions)(IndexTarjetas);
