import React, { Component } from 'react';
import axios from 'axios';
import { URL_DEFAULT } from '../../../urls/urls';

class FormLogin extends Component {

    constructor(props) {
        super(props);
        this.state = {
            loader: false
        }
    }


    login = async () => {
        this.setState({
            loader: true
        });
        const usuario = document.getElementById('usuario').value;
        const pass = document.getElementById('password').value;
        window.localStorage.clear();
        axios.post(URL_DEFAULT + 'users/login', { 'usuario': usuario, 'password': pass })
            .then((res) => {
                if (res.status === 200) {
                    window.localStorage.setItem('token', res.data.token);
                    if (window.localStorage.getItem('token') != '') {
                        this.props.changeLogin();                        
                    }
                }
            })
            .catch((e) => {
                this.setState({
                    loader: false
                });
                console.log(e);
            })
    }
    preloader = () => {
        if (this.state.loader) {
            return <div className="row">
                <div className="center-align">
                    <div className="preloader-wrapper big active">
                        <div className="spinner-layer spinner-blue-only">
                            <div className="circle-clipper left">
                                <div className="circle"></div>
                            </div><div className="gap-patch">
                                <div className="circle"></div>
                            </div><div className="circle-clipper right">
                                <div className="circle"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        }
        return null;
    }

    erroMessage = () => {
        return <div className = "row red darken-1 white-text warning">
            <div className = "col l12 center-align">
                Usuario o contraseña no válidos
            </div>
        </div>
    }
    
    render() {
        return (
            <div className="row">
                <form id="login" className="col s12">
                    <div className="row">
                        <div className="input-field col l12">
                            <input id="usuario" type="text" className="validate" />
                            <label htmlFor="usuario">Usuario</label>
                        </div>
                    </div>
                    <div className="row">
                        <div className="input-field col l12">
                            <input id="password" type="password" className="validate" />
                            <label htmlFor="password">Password</label>
                        </div>
                    </div>
                    {this.preloader()}
                    {this.erroMessage()}
                    <button className="btn waves-effect deep-purple darken-4 waves-light col l12" type="button" onClick={this.login} name="action">SIGN IN</button>
                </form>
            </div>
        );
    }
}
export default FormLogin;
