import React, { Component } from 'react';
import axios from 'axios';
import './IndexPreguntas.css';
import { Link } from 'react-router-dom';


class IndexPreguntas extends Component {
    constructor(props) {
        super(props);
        this.state = {
            preguntas: [
                {
                    'id': 1,
                    'texto': 'texto pregunta 1'
                },
                {
                    'id': 2,
                    'texto': 'texto pregunta 2'
                },
                {
                    'id': 3,
                    'texto': 'texto pregunta 3'
                },
                {
                    'id': 4,
                    'texto': 'texto pregunta 4'
                },
            ]
        }
    }
    componentDidMount() {
        /*const token = window.localStorage.getItem('token');
        const instance = axios.create({
            baseURL: 'http://localhost:8000/api/users',
            timeout: 1000,
            headers: { 'Authorization': 'Bearer ' + token }
        });

        instance.get('http://localhost:8000/api/users')
            .then(res => {
                this.setState({
                    usuarios: res.data.users
                });
            })
            */
    }

    user_to_edit(e) {
        console.log(e);
        //this.props.userToEdit(id);
    }

    ponerFilas = () => this.state.preguntas.map((pregunta, index) => (
        <tr key={pregunta.id}>

            <th scope="row">
                {index + 1}
            </th>
            <td>
                {pregunta.id}
            </td>
            <td>
                {pregunta.texto}
            </td>
            <td>

                <Link id="btn-edit" to={{
                    pathname: "/editar",
                    id: pregunta.id,
                    texto: pregunta.texto
                }} className="valign-wrapper"><i className="material-icons">create</i>EDITAR</Link>

            </td>
        </tr>

    ));

    render() {
        return (

            <div id="index-preguntas" className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="a-right">
                                <Link to='/crearPregunta' id="crear_pregunta" className="valign-wrapper"><i className="material-icons">add</i>CREAR</Link>
                            </div>
                            <table id="table-users">
                                <thead>
                                    <tr>
                                        <th scope="col">#</th>
                                        <th scope="col">Id</th>
                                        <th scope="col">Texto de la pregunta</th>
                                        <th scope="col">Acciones</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {this.ponerFilas()}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>

            </div>


        );
    }
}
export default IndexPreguntas;
