import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');

export const traerRespuestas = (respuestas) => async (dispacth) => {       
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'answers',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_respuestas',
        payload: respuesta.data.respuestas
    })
}

export const crearRespuesta = (respuesta, respuestas) => async (dispacth) => {
    
    const instance = axios.create({
        baseURL: URL_DEFAULT + 'answers',        
        headers: { 'Authorization': 'bearer ' + token }
    });
    const r = await instance.post(URL_DEFAULT + 'answers', respuesta);      
    respuestas.push(r.data.respuestas);
    dispacth({
        type: 'crear_respuesta',
        payload: r.data.respuestas
    })
}

export const editarRespuesta = (data, respuestas) => async (dispacth) => {
    
    respuestas[data.index].texto = data.texto;
    respuestas[data.index].id = data.id;
    
    dispacth({
        type: 'editar_respuesta',
        payload: respuestas
    })
}

export const eliminarRespuesta = (index, respuestas) => async (dispacth) => {
    
    respuestas.splice(index, 1);
    dispacth({
        type: 'eliminar_respuesta',
        payload: respuestas
    })
}
