import React, { Component } from 'react';
import CKEditor from 'ckeditor4-react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasTriviaActions from '../../../../actions/preguntasTriviaActions';

class EditarPreguntaT extends Component {

    constructor(props) {
        super(props);

        this.state = {
            texto: this.props.location.texto,

        };

        this.onEditorTextoChange = this.onEditorTextoChange.bind(this);

    }

    onEditorTextoChange(evt) {
        this.setState({
            texto: evt.editor.getData()
        });
    }

    showSubtemas = () => this.props.subtemas.map((p, index) => (
        <option key={index} value={p.id}>{p.texto}</option>
    ))

    editar = () => {

        let data = {
            "id": this.props.location.id,
            "texto": this.state.texto,
            "feedback_verdadero": document.getElementById('feedbck_positivo').value,
            "feedback_negativo": document.getElementById('feedbck_negativo').value,
            "opcion_correcta": document.getElementById('opc_correcta').value,
            "subtema_id": document.getElementById('subtemas').value,
            "respuestas": [
                {
                    'id': document.getElementById('id_resp_1').value,
                    'texto': document.getElementById('resp1').value
                },
                {
                    'id': document.getElementById('id_resp_2').value,
                    'texto': document.getElementById('resp2').value
                }
            ]
        }
        this.props.editarPreguntaT(data);
    }
delete () {

    this.props.eliminarPreguntaT(this.props.location.id);
}
render() {
    return (
        <div className="row">
            <div className="col l12">
                <div className="card-panel">
                    <div className="card-title"><h5>Editar pregunta</h5></div>
                    <div className="card-content">
                        <div className="row">
                            <form className="col s12">
                                <div className="row">
                                    <p>Texto de la pregunta</p>
                                    <CKEditor onBeforeLoad={ ( CKEDITOR ) => ( CKEDITOR.disableAutoInline = true ) }
                                        data={this.state.texto}
                                        onChange={this.onEditorTextoChange} />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
                <div className="card-panel">
                    <div className="card-title"><h5>Feedbacks</h5></div>
                    <div className="card-content">
                        <div className="row">
                            <div className="input-field col s12">
                                <p>Feedback positivo:</p>
                                <input id="feedbck_positivo" type="text" className="validate" defaultValue={this.props.location.feedback_verdadero} />
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s12">
                                <p>Feedback negativo:</p>
                                <input id="feedbck_negativo" type="text" className="validate" defaultValue={this.props.location.feedback_verdadero} />
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-panel">
                    <div className="card-title"><h5>Respuestas</h5></div>
                    <div className="card-content">
                        <div className="row">
                            <div className="input-field col s12">
                                <input id="id_resp_1" type="hidden" value={this.props.location.id_respuesta1}></input>
                                <p>Respuesta 1:</p>
                                <input id="resp1" type="text" className="validate" defaultValue="Verdadero" defaultValue={this.props.location.respuesta1} />
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s12">
                                <input id="id_resp_2" type="hidden" value={this.props.location.id_respuesta2}></input>
                                <p>Respuesta 2:</p>
                                <input id="resp2" type="text" className="validate" defaultValue="Falso" defaultValue={this.props.location.respuesta2} />
                            </div>
                        </div>
                        <div className="row">
                            <div className="input-field col s6">
                                <p>Opción correcta:</p>
                                <select id="opc_correcta" className="browser-default" defaultValue={this.props.location.opcion_correcta}>
                                    <option value="1">1</option>
                                    <option value="2">2</option>
                                </select>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-panel">
                    <div className="card-title"><h5>subtema asociado</h5></div>
                    <div className="card-content">
                        <div className="row">
                            <div className="input-field col s6">
                                <p>¿A qué subtema pertenece?</p>
                                <select id="subtemas" className="browser-default" defaultValue={this.props.location.subtema_id}>
                                    {this.showSubtemas()}
                                </select>
                            </div>
                        </div>
                    </div>
                    <div className="card-action">
                        <Link className="btn btn-small btn_opts" onClick={this.editar} to='/preguntasT'>Guardar</Link>
                        <Link className="btn btn-small btn_opts" to='/preguntasT'>Cancelar</Link>
                        <Link className="btn btn-small btn_opts" to='/preguntasT' onClick={() => { this.delete() }}>Eliminar</Link>
                    </div>
                </div>
            </div>
        </div>
    );
}
}
const mapToStateProps = (reducers) => {
    return reducers.preguntasTReducers;
}
export default connect(mapToStateProps, preguntasTriviaActions)(EditarPreguntaT);
