import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';
const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'questions';
const urlModulos = URL_DEFAULT + 'modulos';
const urlRespuestas = URL_DEFAULT + 'answers/getById';
const instance = axios.create({
    baseURL: url,
    headers: { 'Authorization': 'bearer ' + token }
});
export const traerPreguntas = () => async (dispacth) => {       
    dispacth({
        type: 'cargando',        
    })
    const respuesta = await instance.get();
    console.log(respuesta.data.preguntas)
    dispacth({
        type: 'traer_preguntas',
        payload: respuesta.data.preguntas
    })
}

export const traerModulos = () => async (dispacth) => {       
    const instance = axios.create({
        baseURL: urlModulos,
        headers: { 'Authorization': 'bearer ' + token }
    });
    const respuesta = await instance.get();    
    dispacth({
        type: 'traer_preguntas',
        payload: respuesta.data.modulos
    })
}

export const crearPregunta = (data, preguntas) => async (dispacth) => {
    
   
    const respuesta = await instance.post(url, data);   
    preguntas.push(respuesta.data.pregunta);
    dispacth({
        type: 'crear_pregunta',
        payload: preguntas
    })
}

export const editarPregunta = (data) => async (dispacth) => {
    

    await instance.put(url, data);
    const respuesta = await instance.get();     
    dispacth({
        type: 'editar_pregunta',
        payload: respuesta.data.preguntas
    })
    dispacth({
        type: 'clear_all_values',
        payload: []
    })
}

export const eliminarPregunta = (id) => async (dispacth) => {
   
   
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get();  
    
    dispacth({
        type: 'eliminar_pregunta',
        payload: respuesta.data.preguntas
    })
}

export const traerRespById = (id) => async (dispacth) => {       
    const instance = axios.create({
        baseURL: urlRespuestas,
        headers: { 'Authorization': 'bearer ' + token }        
    });
    const respuesta = await instance.get(urlRespuestas, { params: {'id': id} });
    let positions = [];
    let texts = [];
    let scores = [];
    let ids = [];
    let respuestas = respuesta.data.respuestas;
    respuestas.map((r) => {
        ids.push(r.id);
        positions.push(r.posicion);
        texts.push(r.texto);
        scores.push(r.puntaje);
    })
    dispacth({
        type: 'clear_all_values',
        payload: []
    })
    dispacth({
        type: 'traer_respuestas',
        payload: respuesta.data.respuestas
    })
    dispacth({
        type: 'set_positions',
        payload: positions
    })
    dispacth({
        type: 'set_texts',
        payload: texts
    })
    dispacth({
        type: 'set_scores',
        payload: scores
    })
    dispacth({
        type: 'set_ids',
        payload: ids
    })

}

export const clearPositionsDefaults = () => async (dispacth) => {       
    
    dispacth({
        type: 'clear_position_defaults',
        payload: []
    })
   
}

export const clearScoresDefaults = () => async (dispacth) => {       
    
    dispacth({
        type: 'clear_scores_defaults',
        payload: []
    })
   
}

export const clearAllValues = () => async (dispacth) => {       
    
    dispacth({
        type: 'clear_all_values',
        payload: []
    })
   
}

export const changeTextValues = (index, value, texts) => async (dispacth) => {       
    let textos = texts.slice();
    textos[index] = value;
    dispacth({
        type: 'change_texts_values',
        payload: textos
    })
   
}
