import React, { Component } from 'react';
import './Crear.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as recursosActions from '../../../../actions/recursosActions';


class CrearRecurso extends Component {



    crear = () => {

        let data = {
            "id": this.props.lastId + 1,
            "nombre": document.getElementById('nombre_recurso').value,
            "url": document.getElementById('url_recurso').value
        }
        this.props.crearRecurso(data, this.props.recursos);

    }

    handleChange = (e) => {
        console.log('changeeee');
        var reader = new FileReader();
        var file = e.target.files[0];
        var output = document.getElementById('output');
        output.src = URL.createObjectURL(e.target.files[0]);
        var imgData = getBase64Image(e.target.files[0]);
        console.log(imgData);
        /*var image = document.getElementById('image');
        image.src = "data:image/jpg;base64," + imgData;
        reader.onload = function (e) {
            console.log('eventtt');
            // El texto del archivo se mostrará por consola aquí
            console.log(e.target.result);
            console.log(reader.result + '->' + typeof reader.result)
            var thisImage = reader.result;
            window.localStorage.setItem("imgData", thisImage);
        };
        /*reader.onload = function (e) {
            console.log(reader.result + '->' + typeof reader.result)
            var thisImage = reader.result;
            window.localStorage.setItem("imgData", thisImage);
        };*/
        //reader.readAsDataURL(this.files[0]);
    }
    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">
                                    <div className="input-field col s6">
                                        <input id="nombre_recurso" type="text" className="validate" />
                                        <label htmlFor="nombre_recurso">Nombre de la recurso</label>
                                    </div>
                                    <div className="input-field col s6">
                                        <input id="url_recurso" onChange={ e => this.handleChange(e) } type="file" className="validate" />
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/recursos'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/recursos'>Cancelar</Link>
                        </div>
                        <img id="output" />
                        <img id="image" />
                    </div>
                </div>
            </div>
        );
    }
}

function getBase64Image(img) {
    var canvas = document.createElement("canvas");
    canvas.width = img.width;
    canvas.height = img.height;

    var ctx = canvas.getContext("2d");
    ctx.drawImage(img, 0, 0);
    return canvas.toDataURL();
    /*var dataURL = canvas.toDataURL("image/jpg");

    return dataURL.replace(/^data:image\/(png|jpg);base64,/, "");*/
}

const mapToStateProps = (reducers) => {
    return reducers.recursosReducers;
}
export default connect(mapToStateProps, recursosActions)(CrearRecurso);
