import axios from 'axios';
import { URL_DEFAULT } from '../urls/urls';

const token = window.localStorage.getItem('token');
const url = URL_DEFAULT + 'tarjetasSaber';
const instance = axios.create({
    baseURL: url,        
    headers: { 'Authorization': 'bearer ' + token }
});

export const traerTarjetasSM = (tarjetas) => async (dispacth) => {       
    dispacth({
        type: 'cargando'        
    })
    const respuesta = await instance.get();
    dispacth({
        type: 'traer_tarjetas_sm',
        payload: respuesta.data.tarjetas
    })
}

export const crearTarjetaSM = (tarjeta, tarjetas) => async (dispacth) => {
      
    const r = await instance.post(url, tarjeta);      
    tarjetas.push(r.data.tarjeta);    
    dispacth({
        type: 'crear_tarjeta_sm',
        payload: tarjetas
    })
}

export const editarTarjetaSM = (data) => async (dispacth) => {
    
    await instance.put(url, data);
    const respuesta = await instance.get(url);   
    dispacth({
        type: 'editar_tarjeta_sm',
        payload: respuesta.data.tarjetas
    })
}

export const eliminarTarjetaSM = (id) => async (dispacth) => {
    
    await instance.delete(url, { params: {"id": id }} );
    
    const respuesta = await instance.get(url);  
    dispacth({
        type: 'eliminar_tarjeta_sm',
        payload: respuesta.data.tarjetas
    })
}
