import React, { Component } from 'react';
import './CrearT.css';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as preguntasTriviaActions from '../../../../actions/preguntasTriviaActions';

class CrearPreguntaT extends Component {



    crear = () => {
        //let idPregunta = this.props.preguntas; 
        console.log(this.props.preguntasT);
        let data = {
            "id": this.props.lastId + 1,
            "texto": document.getElementById('texto_preguntaT').value
        }
        this.props.crearPreguntaT(data, this.props.preguntasT);

    }

    componentDidMount() {


    }

    /*ponerGeneros = () => this.state.generos.map((genero, index) => (
        <option value={genero.id_genero}>
            {genero.nombre_genero}
        </option>
    ));*/

    render() {
        return (
            <div className="row">
                <div className="col l12">
                    <div className="card-panel">
                        <div className="card-title"><h5>Crear pregunta</h5></div>
                        <div className="card-content">
                            <div className="row">
                                <form className="col s6">

                                    <div className="input-field col s6">
                                        <input id="texto_preguntaT" type="text" className="validate" />
                                        <label htmlFor="texto_preguntaT">Texto de la pregunta</label>
                                    </div>
                                </form>
                            </div>
                        </div>
                        <div className="card-action">
                            <Link className="btn btn-primary btn_opts" onClick={this.crear} to='/preguntasT'>Guardar</Link>
                            <Link className="btn btn-primary btn_opts" to='/preguntas'>Cancelar</Link>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

const mapToStateProps = (reducers) => {
    return reducers.preguntasTReducers;
}
export default connect(mapToStateProps, preguntasTriviaActions)(CrearPreguntaT);
